common
{
	progressive_merge = 1
}

index rt
{
    type = rt
    path = /rt
    charset_table = a..z, _, A..Z->a..z	
    dict = keywords
    rt_field = title
    rt_attr_uint = idd
	rt_attr_string = text
}

source src_dummy
{
  type      = mysql

  sql_query     = SELECT id, gid, text FROM test_table
  sql_attr_uint   = gid
}

index test_dummy
{
  source      = src_dummy
  path      = /test_dummy
}

test.db_create:
create table test_table
(
	id int not null,
	text varchar(255) not null,
	gid int
);

test.custom_test:
function RandomWords ( $n, $seed )
{
	srand ( $seed );
	$words = [];
	for ( $k=0; $k<$n; $k++ )
		$words[] = substr ( str_shuffle ( 'abcdefghijklmnopqrstuvwxyz' ), 0, 5 );
	return join ( $words, " " );
}

function RestartDaemon()
{
	$error = "";
	$r = array();
	$stop = StopWaitSearchd ( "config.conf", "searchd.pid" );
	$r[] = 'stoped='.($stop==0 ? 'ok' : 'err' ) . '(code=' . $stop . ')';
	usleep ( 150000 );
	$started = StartSearchd ( "config.conf", "error.txt", "searchd.pid", $error );
	if ( $started == 0 || $started == 2 )
	{
		$r[] = "started=ok";
	} else {
		$r[] = sprintf("start.err=%d local=%s", $started, $error);
	}

	return $r;
}

$results = array();
$ql->Reconnect();

$results[] = 'populate commit';

$id = 1;
$docs = array(60, 500, 100, 150);
foreach ( $docs as $k=>$v )
{
	$rows = [];
	for ( $j=0; $j<$v; $j++ )
	{
		$rows[] = sprintf ( "(%d,'%s','%s', %d)", $id, RandomWords ( 10, $id ), RandomWords ( 1000, $id ), $id );
		$id++;
	}

	$sql = sprintf ( "insert into rt (id, title, text, idd) values\n %s", join ( $rows, ",\n  " ) );
	$results[] = $ql->Query ( $sql );
	$results[] = $ql->Query ( "flush ramchunk rt" );
	$results[] = "\n" . 'flushed ' . $id;
}

$results[] = 'query test';
$results[] = "\n" . $ql->Query ( "SELECT id FROM rt WHERE id > 10 and id < 60 order by id asc limit 3" );
$results[] = "\n" . $ql->Query ( "SELECT id FROM rt WHERE id > 100 and id < 500 order by id asc limit 3"  );
$results[] = "\n" . $ql->Query ( "SELECT id FROM rt WHERE id > 700 and id < 800 order by id asc limit 3"  );

$res = RestartDaemon();
$results = array_merge ( $results, $res );
$ql->Reconnect();
$results[] = "\n" . $ql->Query ( "OPTIMIZE INDEX rt option sync=1" );

$res = RestartDaemon();
$results = array_merge ( $results, $res );
$ql->Reconnect();

$seg = $ql->Query ( "show index rt status" );
$seg = explode ( "\n", $seg );
foreach ( $seg as $k=>$v )
{
	if (0 === strpos($v, 'disk_chunks')) {
	   $results[] = "\n" . $v;
	}
}

$results[] = 'query test';
$results[] = "\n" . $ql->Query ( "SELECT id FROM rt WHERE id > 10 and id < 60 order by id asc limit 3" );
$results[] = "\n" . $ql->Query ( "SELECT id FROM rt WHERE id > 100 and id < 500 order by id asc limit 3"  );
$results[] = "\n" . $ql->Query ( "SELECT id FROM rt WHERE id > 700 and id < 800 order by id asc limit 3"  );
