test.config:
common { lemmatizer_base = lemmatizer_base }
indexer { mem_limit = 16M }
searchd {
}
source plain1 { type = mysql
sql_query_pre = set names utf8 sql_query = select *, 1 as idd from test_table sql_attr_uint = idd }
index plain1 { source = plain1 path = /plain1 regexp_filter = (blue|red) => color stopwords = /stops.txt wordforms = /wordforms.txt morphology = lemmatize_en_all }
index rt1 { type = rt path = /rt1 rt_mem_limit = 8M rt_field = title rt_field = content rt_attr_uint = idd dict = keywords regexp_filter = (blue|red) => color stopwords = /stops.txt wordforms = /wordforms.txt morphology = lemmatize_en_all }
index t480 { type = rt path =/t480 rt_field = content rt_field = f rt_field = f2 rt_field = f3 }
index z480 { type = rt path =/z480 rt_field = title rt_field = content index_zones=aa,bb,cc html_strip = 1 }
index pq1 { type = percolate path = /pq1 rt_field = title rt_field = content rt_attr_uint = idd regexp_filter = (blue|red) => color stopwords = /stops.txt wordforms = /wordforms.txt morphology = lemmatize_en_all }
index tmpl1 : plain1 { type = template path = /tmpl1 }
index exp1 { source = plain1 path = /exp1 }

test.db_create:
CREATE TABLE test_table
(
	id INTEGER PRIMARY KEY NOT NULL,
	content VARCHAR(255) NOT NULL,
	title VARCHAR(255) NOT NULL
) CHARACTER SET utf8;

queries.sphinxql:
INSERT INTO rt1 (id, idd, content, title) VALUES
( 100, 12, 'red baron', 'he runs like me' ),
( 101, 12, 'blue sky','running dog' ),
( 102, 12, 'the box and a toy', 'online color bubles' ),
( 103, 12, 'a black dog is very popular', 'not this time' ),
( 104, 12, 'blue socks and red shoes', 'run in the woods' ),
( 105, 12, 'he told me this', 'like running' );

set profiling=1;

SELECT id FROM plain1 WHERE MATCH('red | sky') limit 1;
show plan;
SELECT id FROM rt1 WHERE MATCH('red | sky') limit 1;
show plan;
EXPLAIN QUERY plain1 'red | sky';
EXPLAIN QUERY rt1 'red | sky';
EXPLAIN QUERY pq1 'red | sky';
EXPLAIN QUERY tmpl1 'red | sky';

SELECT id FROM plain1 WHERE MATCH('(running | he ) !dog') limit 1;
show plan;
SELECT id FROM rt1 WHERE MATCH('(running | he ) !dog') limit 1;
show plan;
EXPLAIN QUERY plain1 '(running | he ) !dog';
EXPLAIN QUERY rt1 '(running | he ) !dog';
EXPLAIN QUERY pq1 '(running | he ) !dog';
EXPLAIN QUERY tmpl1 '(running | he ) !dog';


SELECT id FROM plain1 WHERE MATCH('me story telling @title runs') limit 1;
show plan;
EXPLAIN QUERY plain1 'me story telling @title runs';
EXPLAIN QUERY rt1 'me story telling @title runs';
EXPLAIN QUERY pq1 'me story telling @title runs';
EXPLAIN QUERY tmpl1 'me story telling @title runs';

EXPLAIN QUERY plain1 'the box black dog the !cat';
EXPLAIN QUERY rt1 'the box black dog the !cat';
EXPLAIN QUERY pq1 'the box black dog the !cat';
EXPLAIN QUERY tmpl1 'the box black dog the !cat';

EXPLAIN QUERY plain1 'her @!title box @(title,content) with me';
EXPLAIN QUERY rt1 'her @!title box @(title,content) with me';
EXPLAIN QUERY pq1 'her @!title box @(title,content) with me';
EXPLAIN QUERY tmpl1 'her @!title box @(title,content) with me';


explain query exp1 ' " ( ( a b c ) | d ) e" ';
explain query exp1 ' " ( ( a b c ) | d ) e"~5 ';
explain query exp1 ' " ( ( a b c ) | d ) e"/2 ';

explain query exp1 ' " e ( ( a b c ) | d )" ';
explain query exp1 ' " e ( ( a b c ) | d )"~5 ';
explain query exp1 ' " e ( ( a b c ) | d )"/2 ';

explain query exp1 ' "dead ( alive | poor ) but all ( ( as good ) | ( as fast ) )" ';
explain query exp1 ' "dead ( alive | poor ) but all ( ( as good ) | ( as fast ) )"~5 ';
explain query exp1 ' "dead ( alive | poor ) but all ( ( as good ) | ( as fast ) )"/3 ';

explain query exp1 ' "( a | b | c | aa | bb ) ( d | e | f | dd ) ( g | h | j | gg ) ( k | m | n | kk ) ( o | p | q | oo )" ';
explain query exp1 ' "( a | b | c | aa | bb ) ( d | e | f | dd ) ( g | h | j | gg ) ( k | m | n | kk ) ( o | p | q | oo )"~5 ';
explain query exp1 ' "( a | b | c | aa | bb ) ( d | e | f | dd ) ( g | h | j | gg ) ( k | m | n | kk ) ( o | p | q | oo )"/3 ';


insert into rt1 (id, content) values(1, 'abc'),(2, 'def');
select * from rt1 where match('"(abc|def)"') order by id asc; show meta;
explain query rt1 '"(abc|def)"';

insert into t480 (id, content, f, f2, f3) values (1, '', 'a b', 'a b', ''), (2, '', '', '', 'a b'), (20, 'xray alpha beta', '', '', ''), (21, 'yankee alpha beta', '', '', '');
insert into z480 (id, title, content) values (1, 'a b', ''), (2, '', 'a b'), (20, '<aa> a b </aa>', ''), (21, '<bb> a b </bb>', '<cc> a b </cc>');
set profiling=1;

explain query t480 '"a b" | "a b"';
select min(id) from t480 where match ('"a b" | "a b"'); show plan;
explain query t480 '(@f "a b") | (@f "a b")';
select min(id) from t480 where match ('(@f "a b") | (@f "a b")'); show plan;
explain query t480 '(@f "a b") | (@f2 "a b")';
select min(id) from t480 where match ('(@f "a b") | (@f2 "a b")'); show plan;
explain query t480 '(@f "a b") | (@f[10] "a b")';
select min(id) from t480 where match ('(@f "a b") | (@f[10] "a b")'); show plan;
explain query t480 '(@f[10] "a b") | (@f[10] "a b")';
select min(id) from t480 where match ('(@f[10] "a b") | (@f[10] "a b")'); show plan;
explain query t480 '@content ("xray alpha beta" | "yankee alpha beta")';
select min(id) from t480 where match ('@content ("xray alpha beta" | "yankee alpha beta")'); show plan;
explain query t480 '@content "(xray|yankee) alpha beta"';
select min(id) from t480 where match ('@content "(xray|yankee) alpha beta"'); show plan;
explain query z480 '(ZONE:cc "a b") | (ZONE:bb "a b")';
select min(id) from z480 where match ('(ZONE:cc "a b") | (ZONE:bb "a b")'); show plan;
explain query z480 '@content (ZONE:aa "a b") | (ZONE:bb "a b")';
select min(id) from z480 where match ('@content (ZONE:aa "a b") | (ZONE:bb "a b")'); show plan;
explain query z480 '@content (ZONE:aa "a b") | (ZONE:cc "a b")';
select min(id) from z480 where match ('@content (ZONE:aa "a b") | (ZONE:cc "a b")'); show plan;
explain query z480 '(ZONESPAN:aa "a b") | (ZONESPAN:aa "a b")';
select min(id) from z480 where match ('(ZONESPAN:aa "a b") | (ZONESPAN:aa "a b")'); show plan;
explain query z480 '(ZONE:aa "a b") | (ZONESPAN:aa "a b")';
select min(id) from z480 where match ('(ZONE:aa "a b") | (ZONESPAN:aa "a b")'); show plan;
explain query z480 '(ZONE:aa "a b") | (ZONESPAN:cc "a b")';
select min(id) from z480 where match ('(ZONE:aa "a b") | (ZONESPAN:cc "a b")'); show plan;
explain query z480 '@title (ZONE:aa "a b") | @content (ZONE:cc "a b")';
select min(id) from z480 where match ('@title (ZONE:aa "a b") | @content (ZONE:cc "a b")'); show plan;
