CREATE TABLE test1 (title text) rt_mem_limit='2G';
CREATE TABLE test2 LIKE test1;
SHOW CREATE TABLE test1;
SHOW CREATE TABLE test2;

ALTER TABLE test1 rt_mem_limit='1G';
SHOW CREATE TABLE test1;

drop table test1;
