test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

}

source test
{
	type			= mysql

	sql_query		= select * from test_table

	sql_attr_uint     = price
	sql_attr_bigint   = brand_id
	sql_field_string  = title
	sql_attr_string   = brand_name
	sql_field_string  = property
	sql_attr_multi    = uint categories from field
	sql_attr_json     = j
	sql_attr_float    = coeff
}

index test
{
	source			= test
	path			= /test
}

index test_col
{
	source 			= test
	path			= /test_col

	columnar_attrs 	= price, brand_id, title, brand_name, property
}

index test_col2
{
	source 			= test
	path			= /test_col2

	columnar_attrs 	= price, brand_id, title, brand_name, property
}

index test_dist
{
	type = distributed
	local = test_col
	local = test_col2
}


index pq1
{
	type			= percolate
	path			= /pq1
	rt_field 		= title
	rt_attr_uint	= gid
}

index test_col3
{
	source 			= test
	path			= /test_col3

	columnar_attrs 	= price, brand_id, title, brand_name, property, categories
}

index test_col_rt
{
	type			= rt
	path			= /test_col_rt
	rt_field 		= title
	rt_attr_uint	= gid
}

source cv2_src
{
	type			= mysql

	sql_query		= select * from cv2_table

	sql_attr_bigint     = lastupdateddate
	sql_attr_bigint   = participantidnumber
}

index cv2
{
	source			= cv2_src
	path			= /cv2

    columnar_attrs 	= lastupdateddate, participantidnumber
}

test.db_create:
CREATE TABLE `cv2_table` (
  `id` BIGINT,
  `lastupdateddate` BIGINT,
  `participantidnumber` BIGINT,
  `title` varchar(255)
);

