test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

	thread_stack = 256K
}


source test_json
{
	type			= mysql

	sql_query		= select * from json_src_table
	sql_attr_uint	= gid
	sql_attr_json	= j
    sql_field_string = t1
    sql_attr_string = t2
}

index test_json
{
	source			= test_json
	path			= /test_json
}

queries.sphinxql:
select id, levenshtein('gily', t1) as ord, t1, j.name from test_json order by ord asc;
select id, FLOOR(levenshtein('padre', t1, {normalize=1})*100) as ord, t1, j.name from test_json order by ord asc;
select id, levenshtein('honda', j.name, {normalize=0}) as ord, t1, j.name from test_json order by ord asc;
select id, FLOOR(levenshtein(t1, j.name, {normalize=1})*100) as ord, t1, j.name from test_json order by ord asc;
select id, levenshtein('bandage', t1) as ord, t1, j.name from test_json order by ord asc;

select id, substring_index(t2, ' ', -1) as c1, levenshtein('led', c1) as ord from test_json order by ord asc;

select id, levenshtein('bananamix', t1) as ord, t1 from test_json order by ord asc;
select id, levenshtein('bananamix', t1, {length_delta=4}) as ord, t1 from test_json order by ord asc;
