SELECT id, weight() as score FROM rt_idf WHERE MATCH('burberry') LIMIT 0,200 OPTION global_idf=1, ranker=expr('sum(min_idf)*1000'), idf='plain,tfidf_unnormalized'; show meta;

drop table rt_no_idf;
