test.custom_test:
$results = array ();

$idlist = "";
for ( $i=10000; $i<20000; $i++ )
	$idlist .= "$i,";

$client->SetSelect ( "*, IN(@id,1," . $idlist . "3) AS q" );
$client->SetFilter ( "q", array(1) );

$r = $client->Query ( "" );
$results[] = $r ? $r : $client->GetLastError();
