test.config:
indexer
{
	mem_limit               = 16M
}

searchd
{

}

source delta
{
	type = mysql

	sql_query = select id, text from sph_test;
}

index delta
{
	source = delta
	path = /delta
	charset_table = W,0..9
}

source main
{
	type = mysql

	sql_query = select 2, 'W0000' as text;
}

index main
{
	source = main
	path = /main
	charset_table = W,0..9
}

test.db_create:
create table sph_test
(
	id int not null,
	text varchar(262144) not null
);

test.custom_insert:
$text = '';
for ( $i=0; $i < 1500; $i++ )
	$text .= sprintf ( 'W%04d ', $i );
mysql_query ( "insert into sph_test values ( 1, '$text' )" );
