{
	source = delta
	path = /delta
}

test.db_create:
create table sph_test
(
	id int not null,
	first text(1048576) not null,
	second text(1048576) not null
);

test.custom_insert:
$text = 'C';
for ( $i=0; $i<299991; $i++ )
	$text .= ' C';
$text .= ' B A A A';
mysql_query ( "insert into sph_test values ( 1, '', '$text' )" );
